import loggingimport uvicornfrom fastapi import FastAPIfrom src.tools.perform_health_check import perform_health_checkapp = FastAPI()@app.on_event("startup")def startup():    logging.info("App started.")@app.get("/health_check")def health_check():    health_check_result = perform_health_check()    return health_check_resultif __name__ == '__main__':    uvicorn.run("main:app")