from pydantic import BaseModel, Field, field_validatorfrom src.model.stock_levels_messages import StockLevelsMessagesclass InputData(BaseModel):    unique_request_id: str = Field(...)    branch_id: str = Field(...)    product_id: str = Field(...)    @field_validator('unique_request_id', 'branch_id', 'product_id')    def validate_fields(cls, value: str, field_name: str) -> str:        if not value:            raise ValueError(f'{StockLevelsMessages.missing_field_value}: {field_name}')        return value