import psycopg2import osimport loggingclass ConnectionCreator:    @staticmethod    def create_connection():        try:            conn = psycopg2.connect(                dbname=os.getenv("POSTGRES_DB", "mydatabase"),                user=os.getenv("POSTGRES_USER", "myuser"),                password=os.getenv("POSTGRES_PASSWORD", "mypassword"),                host=os.getenv("POSTGRES_HOST", "db"),                port=os.getenv("POSTGRES_PORT", "5432")            )            logging.info("Error")            return conn        except Exception as e:            logging.error("Error")            return None