import psycopg2import osimport loggingfrom src.model.stock_levels_messages import StockLevelsMessagesclass ConnectionCreator:    @staticmethod    def create_connection():        try:            conn = psycopg2.connect(                dbname=os.getenv("POSTGRES_DB", "mydatabase"),                user=os.getenv("POSTGRES_USER", "myuser"),                password=os.getenv("POSTGRES_PASSWORD", "mypassword"),                host=os.getenv("POSTGRES_HOST", "db"),                port=os.getenv("POSTGRES_PORT", "5432")            )            logging.info(StockLevelsMessages.connection_to_database_successful)            return conn        except Exception as e:            logging.error(f"{StockLevelsMessages.database_connection_error}: {e}")            return None