from fastapi import statusfrom fastapi.responses import JSONResponsefrom src.model.queries_storage import QueriesStoragefrom src.tools.connection_creator import ConnectionCreatorfrom src.tools.stock_levels_logging import stock_levels_loggingdef perform_health_check():    conn = ConnectionCreator.create_connection()    with conn:        cur = conn.cursor()        cur.execute(QueriesStorage.health_check_query)        health_check = cur.fetchone()[0]        stock_levels_logging.info(health_check)        return JSONResponse(            status_code=status.HTTP_200_OK,            content={                "health_check": health_check            }        )